  }

  onSubmit = e =>{
    e.preventDefault();
    console.log("onsubmit")
  }


  render()
  {
    const {email, senha} = this.state

    return (      
      <div>
        <div className="Titulo">
          <h1>{this.state.titulo}</h1>
       </div>
       <div className="Login">
        <form onSubmit={this.onSubmit}>
            <input className="TextBox" type="text" id="email" name="email" value={email}
            onChange={this.onChange}></input>
            <input className="TextBox" type="text" id="senha"  name="senha" value={senha}
            onChange={this.onChange}></input>         
          <div>
            <button type="submit" className="Button"></button>
          </div>
         </form>
       </div>
       <div>
        <label className="Mensagem">{this.state.mensagem}</label>
       </div>
      </div>
    )
  }
} 

export default Login;
