import React, {Component} from 'react';
import "./App.css"

class Login extends Component{

  constructor(props){
    super(props);

    this.state = {email: '',
    senha: ''};

    this.handleChange = this.handleChangeEmail.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChangeSenha = this.handleChangeSenha.bind(this);
  }

  handleChangeEmail(event) {
    this.setState({email: event.target.value});
  }

  handleChangeSenha(event) {
    this.setState({senha: event.target.value});
  }

  handleSubmit(event) {
    console.log(this.state.email);
    console.log(this.state.senha);
    event.preventDefault();
  }

  render() {
    return (
      <div>
      <div className="Titulo">
        <h1>Acesso ao sistema</h1>
      </div>
      <div className="Login">
      <form onSubmit={this.handleSubmit}>
        <label>
          Email: <input type="text" value={this.state.email} onChange={this.handleChangeEmail} />
        </label>
        <label>
          Senha: <input type="text" value={this.state.senha} onChange={this.handleChangeSenha} />
        </label>
        <div>
        <button className="Button" type="submit" value="Submit">Login</button>
        </div>
      </form>
      </div>
      </div>
    );
  }
} 

export default Login;
