import React, {Component} from 'react';
import "./App.css"

class Login extends Component{

  constructor(props){
    super(props);

    this.state = {email: '',
    senha: '',
    mensagem: ''};

    this.handleChangeEmail = this.handleChangeEmail.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChangeSenha = this.handleChangeSenha.bind(this);

  }

  handleChangeEmail(event) {
    this.setState({email: event.target.value});
  }

  handleChangeSenha(event) {
    this.setState({senha: event.target.value});
  }

  handleSubmit(event) {
    event.preventDefault();
  }

    checkLogin = () =>
    {
    if(email == "eduardo.lino@pucpr.br" && senha == "123456") 
    {
      this.state.mensagem = "Acessado com sucesso!"
    }
    else{
      this.state.mensagem = "Usuário ou senha incorretos!"
    }
  }


  render() {
    return (
      <div>
      <div className="Titulo">
        <h1>Acesso ao sistema</h1>
      </div>
      <div className="Login">
      <form onSubmit={this.handleSubmit}>
        <label>
          Email: <input type="text" value={this.state.email} onChange={this.handleChangeEmail} />
        </label>
        <label>
          Senha: <input type="text" value={this.state.senha} onChange={this.handleChangeSenha} />
        </label>
        <div>
        <button className="Button" type="submit" value="Submit">Login</button>
        </div>
      </form>
      </div>
      <div>
        <label className="Menssagem">{this.state.mensagem}</label>
      </div>
      </div>
    );
  }
} 

export default Login;
