import React, {Component} from 'react';
import {css} from "./App.css"

class Login extends Component{

  constructor(props){
    super(props);

    this.state = {
      titulo: "Tela de Login"
    }

    this.mudar= this.mudar.bind(this)
  }

  mudar(){
    var novoTitulo = "O BIXAO TA SOLTO"
    this.setState({titulo:novoTitulo})
  }

  render()
  {
    return (
      <div>
        <div className="Titulo">
          <h1>{this.state.titulo}</h1>
       </div>
       <div className="Login">
        <view style={css.TextBox}>
          <input type="text" id="email" style={css.TextBox} name="email"></input>
          <input type="text" id="senha" style={css.TextBox} name="senha"></input>
          <button onClick={this.mudar}>Mudar Titulo</button>
         </view>
       </div>
      </div>
    )
  }
} 

export default Login;
