import React, {Component} from 'react';
import "./App.css"

class Login extends Component{

  constructor(props){
    super(props);

    this.state = {email: '',
    senha: '',
    mensagem: ''};

    this.handleChangeEmail = this.handleChangeEmail.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChangeSenha = this.handleChangeSenha.bind(this);

  }

  handleChangeEmail(event) {
    this.setState({email: event.target.value});
  }

  handleChangeSenha(event) {
    this.setState({senha: event.target.value});
  }

  handleSubmit(event) {
    event.preventDefault();
    const checkL = this.checkLogin();
    if(checkL == 1)
    {
      this.state.mensagem = "Acessado com sucesso!"
      alert(this.state.mensagem)
    }
    else if(checkL == 0)
    {
      this.state.mensagem = "Usuário ou senha incorretos!"
      alert(this.state.mensagem)
    }
  }

    checkLogin = () =>
    {
    if(this.state.email == "eduardo.lino@pucpr.br" && this.state.senha == "123456") 
    {      
      return 1
    }
    else{
      return 0
    }
  }


  render() {
    return (
      <div>
      <div className="Titulo">
        <h1>Login</h1>
      </div>

      <form onSubmit={this.handleSubmit}>
      <div className="Login">
        <table>
          <tr>
           <input className="TextBox" type="text" value={this.state.email} onChange={this.handleChangeEmail} />
         </tr>
          <tr>
            <input className="TextBox" type="text" value={this.state.senha} onChange={this.handleChangeSenha} />
          </tr>
        </table>
        <div>
        <button className="Button" type="submit" value="Submit">Login</button>
        </div>
        </div>
      </form>
      </div>

    );
  }
} 

export default Login;
