import React, {Component} from 'react';
import "./App.css"

class Login extends Component{

  constructor(props){
    super(props);

    this.state = {
      titulo: "Tela de Login",      
      email: "",
      senha: ""
    }

    this.login_sistema= this.login_sistema.bind(this)
  }

  login_sistema(){
    var msg = "OPA BAO"
    this.setState({mensagem:msg})
    console.log(this.state.email)
    console.log(this.state.senha)
  }

  render()
  {
    return (
      <div>
        <div className="Titulo">
          <h1>{this.state.titulo}</h1>
       </div>
       <div className="Login">
        <view className="TextBox">
          <input className="TextBox" type="text" id="email" name="email" value={this.state.email}></input>
          <input className="TextBox" type="text" id="senha"  name="senha" value={this.state.senha}></input>         
         </view>
         <div>
          <button  className="Button" onClick={this.login_sistema}>Mudar Titulo</button>
         </div>
       </div>
       <div>
        <label className="Mensagem">{this.state.mensagem}</label>
       </div>
      </div>
    )
  }
} 

export default Login;
