import React, {Component} from 'react';
import "./App.css"

class Login extends Component{

  constructor(props){
    super(props);

    this.state = {
      titulo: "Tela de Login",      
      email: "",
      senha: ""
    }

    this.login_sistema= this.login_sistema.bind(this)
  }

  onSubmit = e =>{
    e.preventDefault();
    console.log("onsubmit")
  }

  onChange = e =>{
    this.setState({[e.target.email] : e.target.value})
  }


  render()
  {
    const {email, senha} = this.state

    return (      
      <div>
        <div className="Titulo">
          <h1>{this.state.titulo}</h1>
       </div>
       <div class="Login">
        <form onSubmit={this.onSubmit}>
          <input type="text" value={email} onChange={this.onChange} name={email}></input>
        </form>
       </div>
       <div>
        <label className="Mensagem">{this.state.mensagem}</label>
       </div>
      </div>
    )
  }
} 

export default Login;
